import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './signin.css';

const SignIn = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({ 
    correo_electronico: '', 
    contraseña: '' 
  });

  // Función para validar si ya hay una sesión activa
  useEffect(() => {
    async function validate() {
      try {
        const request = await fetch('http://localhost:3000/validate', {
          credentials: 'include'
        });
        
        if (request.ok) {
          navigate('/table');
        }
      } catch (error) {
        console.error("Error validando sesión:", error);
      }
    }

    validate();
  }, [navigate]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const request = await fetch(
        `http://localhost:3000/login?correo_electronico=${encodeURIComponent(form.correo_electronico)}&contraseña=${encodeURIComponent(form.contraseña)}`,
        {credentials: 'include'}
      );
      
      if (request.ok) {
        navigate('/table');
      } else {
        alert("Usuario o clave incorrecta");
      }
    } catch (error) {
      console.error("Error en login:", error);
      alert("Error de conexión con el servidor");
    }
  };

  return (
    <div className="signin-container">
      <form className="signin-form" onSubmit={handleSubmit}>
        <img 
          src="/img/logo_coffee_background.png" 
          alt="logo" 
          className="header-image" 
        />
        <h2>Sign In</h2>
        <input
          type="email"
          name="correo_electronico"
          placeholder="Email"
          value={form.correo_electronico}
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="contraseña"
          placeholder="Password"
          value={form.contraseña}
          onChange={handleChange}
          required
        />
        <button type="submit">Sign In</button>
      </form>
    </div>
  );
};

export default SignIn;
