import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './signin.css';

const AuthPage = () => {
  const navigate = useNavigate();
  
  // Estado para controlar qué vista mostrar - cambiado a 'signin' para comenzar con login
  const [currentView, setCurrentView] = useState('signin');
  
  const [form, setForm] = useState({ 
    correo_electronico: '', 
    contraseña: '' 
  });

  const [formSignUp, setFormSignUp] = useState({ 
    correo_electronico: '', 
    contraseña: '' 
  });

  // Inicializar table como un array vacío
  const [table, setTable] = useState([]);

  // Actualizar tabla de usuarios
  async function getUsers() {
    try {
      const request = await fetch('http://localhost:3000/usuarios', {
        credentials: 'include'
      });
      
      if (request.ok) {
        const answer = await request.json();
        console.log("Usuarios recibidos:", answer);
        setTable(answer);
      } else {
        console.error("Error en la petición:", await request.text());
      }
    } catch (error) {
      console.error("Error obteniendo usuarios:", error);
    }
  }

  async function deleteUser(id) {
    try {
      const request = await fetch('http://localhost:3000/usuarios?id='+id, {
        credentials: 'include', 
        method: 'DELETE'
      });
      
      if (request.ok) {
        const answer = await request.json();
        setTable(answer);
      }
    } catch (error) {
      console.error("Error eliminando usuarios:", error);
    }
  }

  useEffect(() => {
    async function checkAuth() {
      try {
        const request = await fetch('http://localhost:3000/validate', {
          credentials: 'include'
        });
        
        if (request.ok) {
          // Si el usuario ya está autenticado, ir directamente a la vista de registro
          setCurrentView('signup');
          getUsers(); // Cargar usuarios para la vista de registro
        }
      } catch (error) {
        console.error("Error validando sesión:", error);
      }
    }
    
    // Verificar autenticación al cargar el componente
    checkAuth();
    
    // Cargar usuarios si estamos en la vista de registro
    if (currentView === 'signup') {
      getUsers();
    }
  }, [currentView]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };
  
  const handleChangeSignUp = (e) => {
    setFormSignUp({ ...formSignUp, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const request = await fetch(
        `http://localhost:3000/login?correo_electronico=${encodeURIComponent(form.correo_electronico)}&contraseña=${encodeURIComponent(form.contraseña)}`,
        {credentials: 'include'}
      );
      
      if (request.ok) {
        alert("Inicio de sesión exitoso");
        // Cambiar a la vista de registro después del login exitoso
        setCurrentView('signup');
        getUsers(); // Cargar usuarios para la tabla
      } else {
        alert("Usuario o clave incorrecta");
      }
    } catch (error) {
      console.error("Error en login:", error);
      alert("Error de conexión con el servidor");
    }
  };

  const handleSignUp = async (e) => {
    e.preventDefault();
    
    try {
      const request = await fetch(
        `http://localhost:3000/register?correo_electronico=${encodeURIComponent(formSignUp.correo_electronico)}&contraseña=${encodeURIComponent(formSignUp.contraseña)}`
      );
      
      if (request.ok) {
        alert("Registro exitoso");
        getUsers(); // Actualizamos la tabla después de registrar
        
        // Limpiar formulario de registro
        setFormSignUp({
          correo_electronico: '',
          contraseña: ''
        });
      } else {
        alert("Error al registrar");
      }
    } catch (error) {
      console.error("Error en registro:", error);
      alert("Error de conexión con el servidor");
    }
  };
  
  // Función para navegar a Table.jsx
  const goToTable = () => {
    navigate('/table', { replace: true });
  };

  // Renderizado condicional según la vista actual
  if (currentView === 'signin') {
    // Vista de inicio de sesión
    return (
      <div className="signin-container">
        <img 
          src="/img/logo_coffee_background.png" 
          alt="logo" 
          className="header-image" 
        />
        
        <div className="forms-container">
          {/* Formulario de inicio de sesión */}
          <form className="signin-form" onSubmit={handleSubmit}>
            <h2>Sign In</h2>
            <input
              type="email"
              name="correo_electronico"
              placeholder="Email"
              value={form.correo_electronico}
              onChange={handleChange}
              required
            />
            <input
              type="password"
              name="contraseña"
              placeholder="Password"
              value={form.contraseña}
              onChange={handleChange}
              required
            />
            <button type="submit">Sign In</button>
          </form>
        </div>
      </div>
    );
  } else {
    // Vista de registro y gestión de usuarios
    return (
      <div className="signin-container">
        <img 
          src="/img/logo_coffee_background.png" 
          alt="logo" 
          className="header-image" 
        />
        
        <div className="forms-container">
          {/* Formulario de registro */}
          <form className="signup-form" onSubmit={handleSignUp}>
            <h2>Sign Up</h2>
            <input
              type="email"
              name="correo_electronico"
              placeholder="Email"
              value={formSignUp.correo_electronico}
              onChange={handleChangeSignUp}
              required
            />
            <input
              type="password"
              name="contraseña"
              placeholder="Password"
              value={formSignUp.contraseña}
              onChange={handleChangeSignUp}
              required
            />
            <button type="submit">Sign Up</button>
            
            {/* Botón para navegar a Table.jsx */}
            <button 
              type="button" 
              onClick={goToTable} 
              className="go-to-table-btn"
              style={{
                marginTop: '15px',
                backgroundColor: '#5D4343',
                color: 'white',
                border: 'none',
                padding: '12px',
                borderRadius: '4px',
                cursor: 'pointer',
                width: '100%'
              }}
            >
              Go to Sample Manager
            </button>
          </form>

          {/* Tabla de usuarios registrados */}
          <div className="table-container">
            <h2>Registered Users</h2>
            <table className='users-table'>
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Email</th>
                  <th>Password</th>
                  <th>Delete</th>
                </tr>
              </thead>
              <tbody>
                {table.length > 0 ? (
                  table.map(user => (
                    <tr key={user.id}>
                      <td>{user.id}</td>
                      <td>{user.correo_electronico}</td>
                      <td>{user.contraseña}</td>
                      <td>
                        <button onClick={()=>{deleteUser(user.id)}}>X</button>
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="4">No hay usuarios registrados</td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    );
  }
};

export default AuthPage;
