import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './signin.css';

const SignIn = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({ 
    correo_electronico: '', 
    contraseña: '' 
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevenir el comportamiento predeterminado del formulario

    // Usar los valores del estado form
    const request = await fetch(`http://localhost:3000/login?correo_electronico=${encodeURIComponent(form.correo_electronico)}&contraseña=${encodeURIComponent(form.contraseña)}`, {Credentials:'include'});
    
    
    if (request.ok) {
      alert("Usuario correct0");
      // En lugar de setForm(true), probablemente quieras navegar
      navigate('/table');
    } else {
      alert("Usuario o clave incorrecta");
    }

    }

  async function validate(){
    // Usar los valores del estado form
    const request = await fetch(`http://localhost:3000/validate?correo_electronico=${encodeURIComponent(form.correo_electronico)}&contraseña=${encodeURIComponent(form.contraseña)}`, {Credentials:'include'});
    
    
    if (request.ok) {
      alert("Usuario correcto");
      // En lugar de setForm(true), probablemente quieras navegar
      navigate('/table');
    }

    useEffect(()=>{
      validate()
    },)
  };

  return (
    <div className="signin-container">
      <form className="signin-form" onSubmit={handleSubmit}>
        <img 
          src="/img/logo_coffee_background.png" 
          alt="logo" 
          className="header-image" 
        />
        <h2>Sign In</h2>
        <input
          type="email"
          name="correo_electronico"
          placeholder="Email"
          value={form.correo_electronico}
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="contraseña"
          placeholder="Password"
          value={form.contraseña}
          onChange={handleChange}
          required
        />
        <button type="submit">Sign In</button>
      </form>
    </div>
  );
};

export default SignIn;
