import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './signup.css';

const SignUp = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    nombre: '',
    apellido: '',
    document_type: '',
    document_number: '',
    correo_electronico: '',
    contraseña: '',
    rol: '',
    fecha_nacimiento: ''
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const users = JSON.parse(localStorage.getItem('users')) || [];
    users.push(form);
    localStorage.setItem('users', JSON.stringify(users));
    alert('Registro exitoso');
    navigate('/signin');
  };

  return (
    <div className="container">
      <img src="/img/logo_coffee_background.png.png" alt="logo" className="header-image" />
      <h2>Registration Form</h2>
      <form className="registration-form" onSubmit={handleSubmit}>
        <input type="text" name="nombre" placeholder="Nombre" value={form.nombre} onChange={handleChange} required />
        <input type="text" name="apellido" placeholder="Apellido" value={form.apellido} onChange={handleChange} required />

        <select name="document_type" value={form.document_type} onChange={handleChange} required>
          <option value="">Select the type of document</option>
          <option value="CC">Cédula de Ciudadanía</option>
          <option value="CE">Cédula de Extranjería</option>
          <option value="TI">Tarjeta de Identidad</option>
        </select>

        <input type="number" name="document_number" placeholder="Número de Documento" value={form.document_number} onChange={handleChange} required />
        <input type="email" name="correo_electronico" placeholder="Correo Electrónico" value={form.correo_electronico} onChange={handleChange} required />
        <input type="password" name="contraseña" placeholder="Contraseña" value={form.contraseña} onChange={handleChange} required />

        <select name="rol" value={form.rol} onChange={handleChange} required>
          <option value="">Select a Role</option>
          <option value="Catador">Catador</option>
          <option value="Admin">Admin</option>
          <option value="Usuario">Usuario</option>
        </select>

        <input type="date" name="fecha_nacimiento" placeholder="Date of birth" value={form.fecha_nacimiento} onChange={handleChange} required />

        <button type="submit">Sign Up</button>
      </form>
    </div>
  );
};

export default SignUp;
